<script>
var sse
import CpuChart from '@/components/chart/Cpu.vue'
export default {
  name: 'SystemMonitoring',
  components: {
    CpuChart,
  },
  data() {
    return {
      showTools: true,
      system: {
        host_country: '',
        host_name: '',
        recv_speed: '',
        send_speed: '',
        cpuCountsPhysicalStr: '',
        total_memory: '',
        disk_total: '',
        os_name: '',
        boot_time: '',
        system_load: 0,
        memory_percent: 0,
        swap_percent: 0,
        disk_percent: 0,
      },
      sse: '', //事件实例
    }
  },
  async created() {
    this.getShowTools()
  },
  mounted() {
    if (this.showTools) {
      this.getMonitorData()
    }
  },
  methods: {
    getShowTools() {
      let status = localStorage.getItem('doniaiNavSystemMonitoringShowStatus')
      this.showTools = status === 'true'
    },
    handleShowTools(value) {
      this.showTools = value
      localStorage.setItem('doniaiNavSystemMonitoringShowStatus', value)
      if (!value) {
        sse.close()
      } else {
        this.getMonitorData()
      }
    },
    getMonitorData() {
      if(!! window.EventSource) {
        const sseUrl = process.env.VUE_APP_SSE_URL
        sse = new EventSource(sseUrl)
        sse.onmessage = (event) => {
          let response  = JSON.parse(event.data)
          this.system = response.data
        }

        // 打开事件
        sse.onopen = (event) => { console.log('连接成功')}

        //关闭事件
        sse.onclose = (event) => {console.log('连接关闭')}

        //错误事件
        sse.onerror = (event) => {
          this.showTools = false
        }
      } else {
          alert('您的浏览器不支持SSE')
      }
    },
    getCurrentType(current_percent) {
      if (current_percent > 0 && current_percent <= 30) {
        return 'is-success'
      } else if (current_percent > 30 && current_percent <= 60) {
        return 'is-info'
      } else if (current_percent > 60 && current_percent <= 90) {
        return 'is-warning'
      } else if (current_percent > 90 && current_percent <= 100) {
        return 'is-danger'
      }
    },
  }
}
</script>

